sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {},
    isDatePast: function(strDate) {
        // Create GlideDateTime objects for the current date and the passed in date
        var todaydate = new GlideDateTime().getDate();
        var testdate = new GlideDateTime(strDate).getDate();
        // If the testdate is before todaydate, return true, else return false
        if (testdate.before(todaydate)) {
            return true;
        } else {
            return false;
        }
    },
    isDateToday: function(strDate) {
        var todaydate = new GlideDateTime().getDate();
        var testdate = new GlideDateTime(strDate).getDate();
        if (todaydate.equals(testdate)) {
            return true;
        } else {
            return false;
        }
    },
    type: 'NeedItUtils'
};
